import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  Card,
  CardContent,
  Typography,
  Grid,
  Button,
  TextField,
  Box,
} from '@mui/material';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function PostList() {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '' });

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${API_URL}/posts`);
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/posts`, newPost);
      setNewPost({ title: '', content: '' });
      fetchPosts();
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  return (
    <div>
      <Box component="form" onSubmit={handleSubmit} sx={{ mb: 4 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>创建新文章</Typography>
        <TextField
          fullWidth
          label="标题"
          value={newPost.title}
          onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
          sx={{ mb: 2 }}
        />
        <TextField
          fullWidth
          label="内容"
          multiline
          rows={4}
          value={newPost.content}
          onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
          sx={{ mb: 2 }}
        />
        <Button type="submit" variant="contained" color="primary">
          发布文章
        </Button>
      </Box>

      <Typography variant="h4" sx={{ mb: 3 }}>文章列表</Typography>
      <Grid container spacing={3}>
        {posts.map((post) => (
          <Grid item xs={12} key={post.id}>
            <Card>
              <CardContent>
                <Typography variant="h6" component={Link} to={`/post/${post.id}`} sx={{ textDecoration: 'none', color: 'inherit' }}>
                  {post.title}
                </Typography>
                <Typography color="textSecondary" sx={{ mt: 1 }}>
                  {new Date(post.created_at).toLocaleDateString()}
                </Typography>
                <Typography variant="body2" sx={{ mt: 1 }}>
                  {post.content.substring(0, 200)}...
                </Typography>
                <Typography color="textSecondary" sx={{ mt: 1 }}>
                  评论数: {post.comments_count}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export default PostList;
